import { useState, useRef } from 'react';
import { FaRobot, FaTimes, FaPaperPlane } from 'react-icons/fa';
import axios from 'axios';
import '../styles/'

const Chatbot = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null);

  // Your structured resume data
  const resumeData = {
    skills: ["React", "Python", "AWS", "Docker"],
    projects: ["AI Portfolio Chatbot", "E-commerce Site"],
    experience: ["DevOps Intern at XYZ (2023)"],
    education: ["B.Tech in Computer Science"]
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  const handleSend = async () => {
    if (!input.trim()) return;
    
    const userMessage = { text: input, sender: 'user' };
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    
    try {
      // Using Together AI API (replace with your actual API key)
      const response = await axios.post(
        'https://api.together.xyz/v1/chat/completions',
        {
          model: "mistralai/Mistral-7B-Instruct-v0.1",
          messages: [
            {
              role: "system",
              content: `You are a resume assistant. Use this JSON to answer: ${JSON.stringify(resumeData)}. 
              Be concise and only answer questions about the candidate's background.`
            },
            { role: "user", content: input }
          ],
          max_tokens: 200
        },
        {
          headers: {
            "Authorization": `Bearer ${process.env.REACT_APP_TOGETHER_API_KEY}`
          }
        }
      );
      
      const botMessage = { 
        text: response.data.choices[0].message.content, 
        sender: 'bot' 
      };
      setMessages(prev => [...prev, botMessage]);
    } catch (error) {
      const errorMessage = { 
        text: "Sorry, I couldn't process your request.", 
        sender: 'bot' 
      };
      setMessages(prev => [...prev, errorMessage]);
    }
  };

  return (
    <div className="fixed bottom-8 right-8 z-50">
      {isOpen ? (
        <div className="w-80 h-96 bg-white rounded-lg shadow-xl flex flex-col">
          <div className="bg-green-500 text-white p-3 rounded-t-lg flex justify-between items-center">
            <h3 className="font-bold">Resume Assistant</h3>
            <button onClick={() => setIsOpen(false)}>
              <FaTimes />
            </button>
          </div>
          
          <div className="flex-1 p-3 overflow-y-auto">
            {messages.map((msg, i) => (
              <div 
                key={i} 
                className={`mb-2 p-2 rounded-lg ${msg.sender === 'user' ? 'bg-gray-100 ml-auto' : 'bg-green-100 mr-auto'}`}
              >
                {msg.text}
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>
          
          <div className="p-3 border-t flex">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSend()}
              className="flex-1 border rounded-l-lg p-2"
              placeholder="Ask about my skills..."
            />
            <button 
              onClick={handleSend}
              className="bg-green-500 text-white p-2 rounded-r-lg"
            >
              <FaPaperPlane />
            </button>
          </div>
        </div>
      ) : (
        <button
          onClick={() => setIsOpen(true)}
          className="bg-green-500 text-white p-4 rounded-full shadow-lg hover:bg-green-600 transition"
        >
          <FaRobot size={24} />
        </button>
      )}
    </div>
  );
};

export default Chatbot;
