import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { FaRobot, FaTimes, FaPaperPlane } from 'react-icons/fa';
import '../styles/Chatbot.css';

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);

  // Rinku Yadav's portfolio data
  const portfolioData = `
  ## NAME - RINKU YADAV
  ## EDUCATION
  - **B.Tech in Electronics and Communication Engineering**
    - Malaviya National Institute of Technology, Jaipur (2020-2024)
    - CGPA: 8.02/10
  - **Senior Secondary (12th Grade)**
    - Matrix High School, Sikar, Rajasthan
    - Percentage: 87.20% (PCM)
  - **Secondary (10th Grade)**
    - HDI Public School, Sikar, Rajasthan
    - Percentage: 86.5%

  ## TECHNICAL SKILLS
  - **Programming Languages:** C, C++, HTML, CSS, JavaScript, SQL
  - **Technologies/Frameworks:** React.js, Tailwind CSS
  - **Tools:** VS Code, Git, GitHub
  - **Coursework:** DSA, Operating Systems, Microprocessors, Computer Architecture

  ## PROJECTS
  ### 1. AI-Powered Code Reviewer (Full Stack)
  - **Tech Stack:** React.js, Tailwind CSS, Node.js, Express, Gemini API, MongoDB
  - **Features:**
    - Real-time code suggestions and error detection
    - Integrated AI for code improvements
    - Responsive frontend design

  ### 2. Doctor Appointment Website (Frontend)
  - **Tech Stack:** React.js, Tailwind CSS
  - **Features:**
    - Doctor search and filtering
    - Appointment scheduling system
    - Responsive UI for all devices

  ### 3. Weather Web App
  - **Tech Stack:** HTML, CSS, JavaScript, OpenWeather API
  - **Features:**
    - Real-time weather data display
    - Location detection
    - Temperature conversion

  ## POSITIONS OF RESPONSIBILITY
  - Joint Secretary | Dance Club | MNIT Jaipur
  - Technical Team Member | Sphinx 2023
  - Executive | Cultural Team | Sphinx 2023
  `;

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  const handleSend = async () => {
    if (!input.trim() || isLoading) return;

    // Add user message
    const userMessage = { text: input, sender: 'user' };
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsLoading(true);

    try {
      const response = await axios.post(
        'https://api.together.xyz/v1/chat/completions',
        {
          model: "mistralai/Mistral-7B-Instruct-v0.1",
          messages: [
            {
              role: "system",
              content: `You are an AI assistant for Rinku Yadav's portfolio. 
              Below is Rinku's complete information. Use only this data to answer questions:

              ${portfolioData}

              Guidelines:
              1. Be concise (1-3 sentences max)
              2. Only answer from the provided data
              3. For unknown queries: "That information isn't in Rinku's portfolio"
              4. Keep answers professional`
            },
            { role: "user", content: input }
          ],
          max_tokens: 200,
          temperature: 0.3
        },
        {
          headers: {
            "Authorization": `Bearer ${process.env.REACT_APP_TOGETHER_API_KEY}`,
            "Content-Type": "application/json"
          }
        }
      );

      const botMessage = { 
        text: response.data.choices[0].message.content.trim(),
        sender: 'bot' 
      };
      setMessages(prev => [...prev, botMessage]);
    } catch (error) {
      console.error("Chatbot error:", error);
      setMessages(prev => [...prev, 
        { text: "I'm having trouble accessing Rinku's information right now.", sender: 'bot' }
      ]);
    } finally {
      setIsLoading(false);
      scrollToBottom();
    }
  };

  return (
    <div className="fixed bottom-8 right-8 z-50">
      {isOpen ? (
        <div className="w-80 h-96 bg-white rounded-lg shadow-xl flex flex-col">
          <div className="bg-green-500 text-white p-3 rounded-t-lg flex justify-between items-center">
            <h3 className="font-bold">Rinku's Portfolio Assistant</h3>
            <button onClick={() => setIsOpen(false)} className="focus:outline-none">
              <FaTimes />
            </button>
          </div>
          
          <div className="flex-1 p-3 overflow-y-auto">
            {messages.map((msg, i) => (
              <div 
                key={i} 
                className={`mb-2 p-2 rounded-lg ${msg.sender === 'user' ? 'bg-gray-100 ml-auto' : 'bg-green-100 mr-auto'}`}
              >
                {msg.text}
              </div>
            ))}
            {isLoading && (
              <div className="bg-green-100 p-2 rounded-lg mr-auto w-16">
                <div className="flex space-x-1">
                  <div className="w-2 h-2 bg-green-500 rounded-full animate-bounce"></div>
                  <div className="w-2 h-2 bg-green-500 rounded-full animate-bounce" style={{animationDelay: '0.2s'}}></div>
                  <div className="w-2 h-2 bg-green-500 rounded-full animate-bounce" style={{animationDelay: '0.4s'}}></div>
                </div>
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>
          
          <div className="p-3 border-t flex">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSend()}
              className="flex-1 border rounded-l-lg p-2 focus:outline-none focus:ring-1 focus:ring-green-500"
              placeholder="Ask about Rinku's skills..."
              disabled={isLoading}
            />
            <button 
              onClick={handleSend}
              className={`bg-green-500 text-white p-2 rounded-r-lg ${isLoading ? 'opacity-50' : 'hover:bg-green-600'}`}
              disabled={isLoading}
            >
              <FaPaperPlane />
            </button>
          </div>
        </div>
      ) : (
        <button
          onClick={() => setIsOpen(true)}
          className="bg-green-500 text-white p-4 rounded-full shadow-lg hover:bg-green-600 transition focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2"
          aria-label="Open chatbot"
        >
          <FaRobot size={24} />
        </button>
      )}
    </div>
  );
};

export default Chatbot;
