// src/components/Experience.jsx

import '../styles/Experience.css';

import React, { useState } from "react";


const experienceData = [
  {
    year: "2023 - Present",
    position: "Frontend Developer",
    company: "Abc Corp.",
    description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Libero ab laboriosam.",
  },
  {
    year: "2022 - 2023",
    position: "Full Stack Developer",
    company: "Google",
    description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Libero ab laboriosam.",
  },
  {
    year: "2021 - 2022",
    position: "Backend Developer",
    company: "YouTube",
    description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Libero ab laboriosam.",
  },
  {
    year: "2020 - 2021",
    position: "Frontend Developer",
    company: "Xyz Inc.",
    description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Libero ab laboriosam.",
  },
];

const Experience = () => {
  const [activeTab, setActiveTab] = useState("Experience");

  return (
    <section className="experience-section">
      <div className="experience-sidebar">
        <h2>Why Hire Me?</h2>
        <p>Lorem ipsum dolor sit amet consectetur adipisicing elit.</p>
        <div className="tabs">
          {["Experience", "Education", "Skills", "About Me"].map((tab) => (
            <button
              key={tab}
              className={activeTab === tab ? "tab active" : "tab"}
              onClick={() => setActiveTab(tab)}
            >
              {tab}
            </button>
          ))}
        </div>
      </div>
      
      <div className="experience-content">
        <h2 className="experience-title">
          <span className="blue-text">My</span> <span className="green-text">Experience</span>
        </h2>
        <p>Lorem ipsum dolor sit amet consectetur adipisicing elit.</p>

        <div className="experience-grid">
          {experienceData.map((exp, index) => (
            <div key={index} className="experience-card">
              <p className="experience-year">{exp.year}</p>
              <h3 className="experience-position">{exp.position}</h3>
              <p className="experience-company">‚óè {exp.company}</p>
              <p className="experience-description">{exp.description}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Experience;
