import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Github, Linkedin, Youtube, TowerControl as GameController, Download } from 'lucide-react';
import '../styles/Hero.css';

function Hero() {
    const [role, setRole] = useState('');
    const [isDeleting, setIsDeleting] = useState(false);
    const [loopNum, setLoopNum] = useState(0);
    const [typingSpeed, setTypingSpeed] = useState(150);
  
    const roles = ['Coder', 'Web Developer', 'Designer', 'Programmer'];
    
    useEffect(() => {
      const handleTyping = () => {
        const current = loopNum % roles.length;
        const fullText = roles[current];
        
        setRole(isDeleting 
          ? fullText.substring(0, role.length - 1)
          : fullText.substring(0, role.length + 1)
        );
        
        setTypingSpeed(isDeleting ? 30 : 150);
        
        if (!isDeleting && role === fullText) {
          setTimeout(() => setIsDeleting(true), 1000);
        } else if (isDeleting && role === '') {
          setIsDeleting(false);
          setLoopNum(loopNum + 1);
        }
      };
  
      const timer = setTimeout(handleTyping, typingSpeed);
      return () => clearTimeout(timer);
    }, [role, isDeleting, loopNum, roles]);
  return (
    <div className="hero-container">
      {/* Hero Section */}
      <section className="hero-section">
        <div className="hero-content-container">
          <div className="hero-grid">
            <motion.div
              initial={{ opacity: 0, x: -50 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.5 }}
              className="hero-text-content"
            >
              <h2 className="hero-title">Lucius Meshach</h2>
              <div className="hero-subtitle-container">
                <span className="hero-subtitle-text">I'm a</span>
                <span className="hero-subtitle-highlight">Coder
                {role}
                  <span className="typing-cursor">|</span>
                </span>
              </div>
              <p className="hero-description">
                Lorem ipsum dolor sit amet consectetur adipisicing elit. Ex, cum eos. Iure incidunt 
                excepturi totam aperiam, deserunt quidem obcaecati consectetur volupt, recusandae 
                quasi sequi. Corrupti officiis nulla excepturi totam.
              </p>
              <div className="hero-actions">
                <motion.a
                  href="/cv.pdf"
                  className="download-button"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  <Download size={20} />
                  Download CV
                </motion.a>
                <div className="social-icons">
                  <motion.a
                    href="https://github.com"
                    target="_blank"
                    rel="noopener noreferrer"
                    className="social-icon"
                    whileHover={{ scale: 1.1 }}
                  >
                    <Github size={20} />
                  </motion.a>
                  <motion.a
                    href="https://linkedin.com"
                    target="_blank"
                    rel="noopener noreferrer"
                    className="social-icon"
                    whileHover={{ scale: 1.1 }}
                  >
                    <Linkedin size={20} />
                  </motion.a>
                  <motion.a
                    href="https://youtube.com"
                    target="_blank"
                    rel="noopener noreferrer"
                    className="social-icon"
                    whileHover={{ scale: 1.1 }}
                  >
                    <Youtube size={20} />
                  </motion.a>
                  <motion.a
                    href="#gaming"
                    className="social-icon"
                    whileHover={{ scale: 1.1 }}
                  >
                    <GameController size={20} />
                  </motion.a>
                </div>
              </div>
            </motion.div>
            <motion.div
              initial={{ opacity: 0, scale: 0.5 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.5 }}
              className="profile-image-container"
            >
              <div className="profile-image-wrapper">
                <img
                  src="https://images.unsplash.com/photo-1568602471122-7832951cc4c5?q=80&w=1000&auto=format&fit=crop"
                  alt="Profile"
                  className="profile-image"
                />
              </div>
              <div className="profile-glow" />
              <div className="profile-border-1" />
              <div className="profile-border-2" />
            </motion.div>
          </div>
        </div>
      </section>
    </div>
  );
}

export default Hero;
